const items = $input.all();
let tableRows = '';

for (const item of items) {
  const data = item.json;

  // --- 1. Status Badge Logic ---
  let statusClass = 'info';
  const statusCodeStr = String(data.status_code);
  if (statusCodeStr.startsWith('2')) statusClass = 'success';
  else if (statusCodeStr.startsWith('4') || statusCodeStr.startsWith('5')) statusClass = 'error';
  else if (statusCodeStr.startsWith('3')) statusClass = 'redirect';
  // Check for DNS/connection errors - use purple/magenta color
  else if (statusCodeStr.includes('ECONNREFUSED') || 
           statusCodeStr.includes('ENOTFOUND') || 
           statusCodeStr.includes('EAI_AGAIN') ||
           statusCodeStr.includes('EAIAGAIN') ||
           statusCodeStr.includes('ENETUNREACH') ||
           statusCodeStr.includes('ETIMEDOUT') ||
           statusCodeStr.includes('ECONNRESET') ||
           statusCodeStr.includes('ECONNABORTED')) statusClass = 'dns-error';

  const statusBadge = `<span class="status-badge status-${statusClass}">${statusCodeStr}</span>`;

  // --- 2. Page Assessment Tags Logic ---
  const generateTagsHTML = (pageAssessment) => {
    if (!pageAssessment || !Array.isArray(pageAssessment) || pageAssessment.length === 0) {
      return '<div class="tags-container"></div>';
    }

    // Simple hash function for consistent colors
    const hashString = (str) => {
      let hash = 0;
      for (let i = 0; i < str.length; i++) {
        const char = str.charCodeAt(i);
        hash = ((hash << 5) - hash) + char;
        hash = hash & hash; // Convert to 32-bit integer
      }
      return Math.abs(hash) % 360;
    };

    const tagBadges = pageAssessment.map(tag => {
      // Clean tag for CSS class (lowercase, replace spaces/special chars with hyphens)
      const cssClass = tag.toLowerCase().replace(/\s+/g, '-').replace(/[^a-z0-9-]/g, '');

      // Format display text: remove hyphens, add spaces, uppercase
      const displayText = tag.replace(/-/g, ' ').toUpperCase();

      // Generate consistent color hash for unknown tags
      const colorHash = hashString(tag.toLowerCase());

      return `<span class="tag-badge tag-${cssClass}" style="--tag-hash: ${colorHash}">${displayText}</span>`;
    }).join('');

    return `<div class="tags-container">${tagBadges}</div>`;
  };

  const assessmentTags = generateTagsHTML(data.page_assessment);

  // --- 3. Data Preparation ---
  const summary = data.agent_summary ? data.agent_summary.replace(/</g, "&lt;").replace(/>/g, "&gt;") : '';
  const displayUrl = data.url; // No more truncation
  const externalLinkIcon = `<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="external-link-icon"><path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6"></path><polyline points="15 3 21 3 21 9"></polyline><line x1="10" y1="14" x2="21" y2="3"></line></svg>`;
  const urlLink = data.url ? `<a href="${data.url}" title="${data.url}" target="_blank" rel="noopener noreferrer" class="link"><span class="url-text">${displayUrl}</span>${externalLinkIcon}</a>` : '';

  let screenshotContent = '';
  if (data.screenshot_url) {
    screenshotContent = `<img src="${data.screenshot_url}" alt="Thumbnail for ${data.domain}" class="screenshot-thumbnail" data-full-src="${data.screenshot_url}">`;
  }

  // --- 4. Individual Date and Source Columns ---
  const formatDate = (dateStr) => {
    if (!dateStr) return '';
    try {
      const date = new Date(dateStr);
      return date.toLocaleString('en-US', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        hour12: true
      });
    } catch {
      return '';
    }
  };

  const createdAt = formatDate(data.created_at);
  const lastScanTime = formatDate(data.last_scan_time);
  const sourceFilename = data.source_filename || 'Unknown source';

  // --- 5. Build the Final HTML Table Row with Individual Columns ---
  tableRows += `
    <tr data-id="${data.id}">
      <td class="checkbox-cell">
        <input type="checkbox" class="row-selector" value="${data.id}" title="Select this row">
      </td>
      <td data-label="URL">${urlLink}</td>
      <td data-label="Status">${statusBadge}</td>
      <td data-label="Summary">
        <div class="summary-section">
          <p class="summary-text">${summary}</p>
        </div>
      </td>
      <td data-label="Tags">${assessmentTags}</td>
      <td data-label="Screenshot">${screenshotContent}</td>
      <td data-label="Created Date">${createdAt}</td>
      <td data-label="Last Scan">${lastScanTime}</td>
      <td data-label="Source">${sourceFilename}</td>
    </tr>
  `;
}

return [{ json: { htmlRows: tableRows } }];
